var P = require('../index');
var p = new P.Client('tester');

p.Organizations.get(289, function(err, org) {
	console.log(org);

	org.set('address_formatted_address', 'asdasd');
	org.set('address_lat', -10);
	org.set('address_long', 10);

	org.save(function() {
		p.Organizations.get(289, function(err, orgUpdated) {
			console.log('saved');
			console.log(orgUpdated);
		});
	});
});